INSERT INTO NOTICE (WRITER_ADMIN_ID, NOTICE_TITLE, NOTICE_DETAIL, IS_MAIN, IS_NEED_PIN, PRIORITY, NOTICE_STATUS, POSTED_AT, VIEW_COUNT)
