mapper.update:
UPDATE SHOPPING_CART


    adult_count = #{adultCount},


    child_count = #{childCount}


WHERE cart_id = #{cartId}

mapper.select:
SELECT *
FROM SHOPPING_CART
WHERE customer_id = #{customerId}
AND room_id = #{roomId}

SELECT *
FROM SHOPPING_CART
WHERE customer_id = #{customerId}
  AND cart_id = #{cartId}
